mapper.select:
SELECT ub.used_book_id, ub.title, ub.discount, ub.description, ub.created_at, ub.status, u.user_id, u.user_nickname, bc.book_condition_id, bc.condition, bi.book_id, bi.book_name, bi.book_author, bi.book_price, bi.book_publisher, bi.book_publication_date, bi.book_isbn, ubi.used_book_image_id, ubi.original_image_name, ubi.stored_image_name FROM used_book ub JOIN users u ON ub.user_id = u.user_id JOIN book_condition bc ON ub.book_condition_id = bc.book_condition_id JOIN book_information bi ON ub.book_id = bi.book_id LEFT JOIN used_book_image ubi ON ub.used_book_id = ubi.used_book_id
ub.title LIKE CONCAT('%', #{searchKey}, '%')
GROUP BY ub.used_book_id ORDER BY ub.used_book_id DESC LIMIT #{pageRequest.size} OFFSET #{pageRequest.skip}

select book_id
from book_information
where book_isbn = #{book_isbn}

mapper.update:
update used_book
set title = #{title}, discount = #{discount}, description = #{description}, book_id = #{book_id}, book_condition_id = #{book_condition_id}, image_check = #{image_check}
where used_book_id = #{used_book_id}
